import React from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';
import Responsive from '../commons/Responsive';

const ItemHeaderBlock = styled.div`
  margin-top: 1.5rem;
  width: 100%;
  background: white;
  position: fixed;
  //padding-left: 3rem;
`;

const ItemHeaderWrapper = styled(Responsive)`
  display: flex;
  justify-content: flex-start;
  //width: 100%
  //padding-left: 3rem;
  .item {
    display: flex;
  }
`;

const ItemImage = styled.div`
  background: black;
  width: 300px;
  height: 300px;
`;

const Blank = styled.div`
  width: 100px;
`;

const ItemHeader = styled.div`
  //position: fixed;
  padding-bottom: 1rem;
  margin-bottom: 1rem;
  background: white;
  h1 {
    font-size: 2rem;
    line-height: 1.5;
    margin: 0;
  }
`;

const SubInfo = styled.div`
  margin-top: 1rem;
  color: ${palette.gray[6]};

  span + span:before {
    color: ${palette.gray[6]};
    padding-left: 0.25rem;
    padding-right: 0.25rem;
    content: '\\B7';
  }
`;

const Tags = styled.div`
  margin-top: 0.5rem;
  .tag {
    display: inline-block;
    color: ${palette.orange[7]};
    text-decoration: none;
    margin-right: 0.5rem;
    &:hover {
      color: ${palette.orange[5]};
    }
  }
`;

const ItemContent = styled(Responsive)`
  font-size: 1.3125rem;
  color: ${palette.gray[8]};
  width: 100%;
`;

const Spacer2 = styled.div`
  height: 10rem;
`;

const ItemViewer = ({ item, error, loading }) => {
  if (error) {
    console.log(error);
    if (error.response && error.response.status === 404) {
      return <ItemHeaderBlock>존재하지 않는 아이템입니다.</ItemHeaderBlock>;
    }
    return <ItemHeaderBlock>오류</ItemHeaderBlock>;
  }

  if (loading || !item) {
    return null;
  }

  console.log(item);

  let { id, name, content, price, tags } = item;

  return (
    <div>
      <ItemHeaderBlock>
        <ItemHeaderWrapper>
          <ItemImage className={item}>adsf</ItemImage>
          <Blank className={item} />
          <ItemHeader className={item}>
            <h1>{name}</h1>
            <SubInfo>
              <span>
                <b>{price}</b>
              </span>
            </SubInfo>
            <Tags>
              {tags.map((tag) => (
                <div className={'tag'}>#{tag}</div>
              ))}
            </Tags>
          </ItemHeader>
        </ItemHeaderWrapper>
      </ItemHeaderBlock>
      <Spacer2 />
      <ItemContent dangerouslySetInnerHTML={{ __html: content }} />
    </div>
  );
};

export default ItemViewer;
