import React from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';
import Responsive from '../commons/Responsive';
import Button from '../commons/Button';

const CartBlock = styled.div`
  width: 100%;
  margin-top: 1.5rem;
`;

const CartWrapper = styled(Responsive)`
  h3 {
    font-size: 2rem;
    color: ${palette.gray[8]};
    padding-bottom: 1rem;
    border-bottom: ${palette.gray[4]} 1px solid;
  }
`;

const ItemWrapper = styled.div`
  height: 20%;
  padding-bottom: 1rem;
  display: flex;
`;

const ItemImage = styled.img`
  max-width: 20%;
  height: auto;
`;

const Blank = styled.div`
  width: 50px;
`;

const ItemInfo = styled.div`
  h4 {
    font-size: 1.5rem;
    color: ${palette.gray[7]};
  }
`;

const Cart = () => {
  return (
    <>
      <CartBlock>
        <CartWrapper>
          <h3>장바구니</h3>
          <ItemWrapper>
            <ItemImage
              src={
                'https://image.msscdn.net/images/goods_img/20191105/1214164/1214164_3_500.jpg'
              }
            />
            <Blank />
            <ItemInfo>
              <h4>title</h4>
            </ItemInfo>
          </ItemWrapper>
          <ItemWrapper>
            <h4>title</h4>
            <ItemImage></ItemImage>
          </ItemWrapper>
        </CartWrapper>
      </CartBlock>
    </>
  );
};

export default Cart;
