import { createAction, handleActions } from 'redux-actions';
import createRequestSaga, {
  createRequestActionTypes,
} from '../lib/createRequestSaga';
import * as itemsAPI from '../lib/api/items';
import { takeLatest } from 'redux-saga/effects';

const INITIALIZE = 'items/INITIALIZE';
const CHANGE_FIELD = 'items/CHANGE_FIELD';
const [
  INPUT_ITEMS,
  INPUT_ITEMS_SUCCESS,
  INPUT_ITEMS_FAILURE,
] = createRequestActionTypes('items/INPUT_ITEMS');

export const initialize = createAction(INITIALIZE);
export const changeField = createAction(CHANGE_FIELD, ({ key, value }) => ({
  key,
  value,
}));
const inputItem = createAction(
  INPUT_ITEMS,
  ({ name, content, price, tags }) => ({ name, content, price, tags }),
);

export function* inputSaga() {
  yield takeLatest(INPUT_ITEMS);
}

const initialState = {
  name: '',
  content: '',
  price: '',
  tags: [],
};

const items = handleActions(
  {
    [INITIALIZE]: (state) => initialState,
    [CHANGE_FIELD]: (state, { payload: { key, value } }) => ({
      ...state,
      [key]: value,
    }),
  },
  initialState,
);

export default items;
