import React, { useCallback, useEffect, useState } from 'react';
import Register from '../../components/indexPage/Register';
import { useSelector, useDispatch } from 'react-redux';
import {
  changeField,
  initialize,
  registerUser,
  registerCheck,
} from '../../modules/register';
import { withRouter } from 'react-router-dom';
import ErrorModal from '../../components/commons/ErrorModal';

const RegisterContainer = () => {
  const [modal, setModal] = useState(false);
  const [text, setText] = useState('');
  const [register, setRegister] = useState(false);
  const dispatch = useDispatch();
  const {
    name,
    email,
    image,
    password,
    check,
    user,
    userError,
    history,
  } = useSelector(({ register }) => ({
    name: register.name,
    email: register.email,
    image: register.image,
    password: register.password,
    check: register.check,
    user: register.user,
    userError: register.userError,
  }));

  const onChangeField = useCallback(
    (payload) => dispatch(changeField(payload)),
    [dispatch],
  );

  const onCheck = () => {
    dispatch(registerCheck({ email }));
    setModal(true);
    if (!check) {
      setText('사용할 수 있습니다.');
    } else {
      setText('이미 사용 중입니다.');
    }
  };

  const onRegister = () => {
    dispatch(registerUser({ name, email, password, image }));
    setRegister(true);
  };

  const onConfirm = () => {
    setModal(false);
  };

  useEffect(() => {
    return () => {
      dispatch(initialize());
    };
  }, [dispatch]);

  return (
    <>
      <Register
        onChangeField={onChangeField}
        name={name}
        email={email}
        password={password}
        image={image}
        check={check}
        onRegister={onRegister}
        onCheck={onCheck}
        modal={modal}
        onConfirm={onConfirm}
        text={text}
      />
      <ErrorModal
        visible={register}
        title={'회원가입 완료'}
        description={'로그인 화면으로 이동합니다.'}
      />
    </>
  );
};

export default withRouter(RegisterContainer);
