import React from 'react';
import styled from 'styled-components';
import Button from '../commons/Button';
import palette from '../../lib/styles/palette';
import Responsive from '../commons/Responsive';

const RegisterBlock = styled(Responsive)`
  margin-top: 1.5rem;
  padding-top: 2rem;
  padding-bottom: 3rem;
  padding-left: 17%;

  h3 {
    font-size: 2rem;
    padding-bottom: 2rem;
  }

  h4 {
    font-size: 1.3rem;
    color: ${palette.gray[7]};
  }
`;

const StyledInput = styled.input`
  padding-left: 1rem;
  border-radius: 4px;
  border: 0.5px ${palette.gray[5]} solid;
  font-size: 1rem;
  height: 2.5rem;
  width: 50%;
  margin-bottom: 5px;
`;

const RegisterButton = styled(Button)`
  margin-top: 2rem;
  font-size: 1.3rem;
  width: 50%;
  height: 4rem;
`;

const SubInfoBlock = styled.div`
  display: flex;
  align-items: center;
  p {
    color: ${palette.gray[5]};
  }
  a {
    padding-left: 1rem;
    color: black;
  }
`;

const Register = () => {
  return (
    <>
      <RegisterBlock>
        <h3>회원 가입</h3>
        <h4>아이디</h4>
        <StyledInput placeholder={'아이디를 입력하세요'} />
        <h4>이메일</h4>
        <StyledInput placeholder={'이메일을 입력하세요'} />
        <h4>비밀번호</h4>
        <StyledInput placeholder={'비밀번호를 입력하세요'} type={'password'} />
        <div>
          <RegisterButton orange>회원 가입</RegisterButton>
        </div>
        <SubInfoBlock>
          <p>이미 회원이신가요?</p>
          <a href={'/login'}>로그인하기</a>
        </SubInfoBlock>
      </RegisterBlock>
    </>
  );
};

export default Register;
