import React, { useEffect, useState } from 'react';
import GoogleLogin from 'react-google-login';
import Button from '../commons/Button';
import palette from '../../lib/styles/palette';
import client from '../../lib/api/client';
import { useDispatch, useSelector } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { loginUser } from '../../modules/login';
import styled from 'styled-components';
import ErrorModal from '../commons/ErrorModal';

const LoginBlock = styled.div`
  margin-top: 1.5rem;
`;

const Login = ({ history }) => {
  const dispatch = useDispatch();

  let { user, userError } = useSelector(({ login }) => ({
    user: login.user,
    userError: login.userError,
  }));

  const [modal, setModal] = useState(false);

  const onConfirm = () => {
    console.log('confirm');
    history.push('/');
  };

  const OnSuccess = (response) => {
    console.log(response);
    const name = response.profileObj.name;
    const email = response.profileObj.email;
    const image = response.profileObj.imageUrl;

    dispatch(loginUser({ name, email, image }));

    console.log(name);
  };

  useEffect(() => {
    if (user) {
      history.push('');
    }
    if (userError) {
      setModal(true);
    }
  }, [modal, user, userError]);

  return (
    <div>
      <LoginBlock>
        <GoogleLogin
          clientId="429509298852-vnmva42a5utbrgnrtpemplvs5uqt4pvd.apps.googleusercontent.com"
          onSuccess={OnSuccess}
          onFailure={(result) => console.log(result)}
          cookiePolicy={'single_host_origin'}
        />
        <ErrorModal
          visible={modal}
          title={'오류'}
          description={'이미 가입돼어 있습니다.'}
          confirmText={'확인'}
          onConfirm={onConfirm}
        />
      </LoginBlock>
    </div>
  );
};

export default withRouter(Login);
