import React, { useEffect, useState } from 'react';
import GoogleLogin from 'react-google-login';
import Button from '../commons/Button';
import palette from '../../lib/styles/palette';
import { useDispatch, useSelector } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { loginUser } from '../../modules/login';
import styled from 'styled-components';
import Responsive from '../commons/Responsive';

const LoginBlock = styled(Responsive)`
  margin-top: 1.5rem;
  padding-top: 2rem;
  padding-bottom: 3rem;
  padding-left: 17%;

  h3 {
    font-size: 2rem;
    padding-bottom: 2rem;
  }

  h4 {
    font-size: 1.3rem;
    color: ${palette.gray[7]};
  }
`;

const StyledInput = styled.input`
  padding-left: 1rem;
  border-radius: 4px;
  border: 0.5px ${palette.gray[5]} solid;
  font-size: 1rem;
  height: 2.5rem;
  width: 50%;
  margin-bottom: 5px;
`;

const Login = ({ history }) => {
  const OnSuccess = (response) => {
    console.log(response);
    const name = response.profileObj.name;
    const email = response.profileObj.email;
    const image = response.profileObj.imageUrl;
  };

  return (
    <>
      <LoginBlock>
        <h3>로그인</h3>
        <h4>이메일</h4>
        <StyledInput placeholder={'이메일을 입력하세요'} />
        <h4>비밀번호</h4>
        <StyledInput />
        <div>
          <Button to={'/register'}>회원가입</Button>
          <GoogleLogin
            clientId="429509298852-vnmva42a5utbrgnrtpemplvs5uqt4pvd.apps.googleusercontent.com"
            onSuccess={OnSuccess}
            onFailure={(result) => console.log(result)}
            cookiePolicy={'single_host_origin'}
          />
        </div>
      </LoginBlock>
    </>
  );
};

export default withRouter(Login);
