import React from 'react';
import styled from 'styled-components';
import Button from '../commons/Button';
import palette from '../../lib/styles/palette';
import Responsive from '../commons/Responsive';

const RegisterBlock = styled(Responsive)`
  margin-top: 1.5rem;
  padding-top: 2rem;
  padding-bottom: 3rem;
  padding-left: 15%;

  h3 {
    font-size: 2rem;
    padding-bottom: 2rem;
  }

  h4 {
    font-size: 1.5rem;
    color: ${palette.gray[7]};
  }
`;

const StyledInput = styled.input`
  padding-left: 1rem;
  border-radius: 4px;
  border: 0.5px ${palette.gray[5]} solid;
  font-size: 1.5rem;
  height: 2.5rem;
  width: 50%;
`;

const RegisterButton = styled(Button)`
  margin-top: 1.5rem;
  font-size: 1rem;
`;

const Register = () => {
  return (
    <>
      <RegisterBlock>
        <h3>회원 가입</h3>
        <h4>아이디</h4>
        <StyledInput placeholder={'아이디를 입력하세요'} />
        <h4>이메일</h4>
        <StyledInput placeholder={'이메일을 입력하세요'} />
        <h4>비밀번호</h4>
        <StyledInput placeholder={'비밀번호를 입력하세요'} />
        <div>
          <RegisterButton>회원 가입</RegisterButton>
        </div>
      </RegisterBlock>
    </>
  );
};

export default Register;
